mapper.update:
UPDATE give

     roundId= #{give.roundId,jdbcType=BIGINT},
     fromPlayerId= #{give.fromPlayerId,jdbcType=BIGINT},
     toPlayerId= #{give.toPlayerId,jdbcType=BIGINT},
     unit= #{give.unit,jdbcType=INTEGER}

WHERE giveId = #{give.giveId,jdbcType=BIGINT}

mapper.select:
select

from give
where 1=1

    and giveId=#{giveId,jdbcType=BIGINT}


    and roundId=#{roundId,jdbcType=BIGINT}


    and fromPlayerId=#{fromPlayerId,jdbcType=BIGINT}


    and toPlayerId=#{toPlayerId,jdbcType=BIGINT}


    and unit=#{unit,jdbcType=INTEGER}


    limit #{offset},#{limit}

select
count(1)
from give
where 1=1

    and giveId=#{giveId,jdbcType=BIGINT}


    and roundId=#{roundId,jdbcType=BIGINT}


    and fromPlayerId=#{fromPlayerId,jdbcType=BIGINT}


    and toPlayerId=#{toPlayerId,jdbcType=BIGINT}


    and unit=#{unit,jdbcType=INTEGER}


    limit #{offset},#{limit}
