mapper.update:
UPDATE user
openId= #{user.openId,jdbcType=VARCHAR}, nickName= #{user.nickName,jdbcType=VARCHAR}, gender= #{user.gender,jdbcType=INTEGER}, country= #{user.country,jdbcType=VARCHAR}, province= #{user.province,jdbcType=VARCHAR}, city= #{user.city,jdbcType=VARCHAR}, avatarUrl= #{user.avatarUrl,jdbcType=VARCHAR}, `language`= #{user.language,jdbcType=VARCHAR}, unionId= #{user.unionId,jdbcType=VARCHAR}, sessionKey= #{user.sessionKey,jdbcType=VARCHAR}, lastLoginTime= #{user.lastLoginTime,jdbcType=TIMESTAMP}, updateTime= #{user.updateTime,jdbcType=TIMESTAMP}, inviteUserId= #{user.inviteUserId,jdbcType=BIGINT}
WHERE userId = #{user.userId,jdbcType=BIGINT}

mapper.select:
select

from user
where 1=1

    and userId=#{userId,jdbcType=BIGINT}


    and openId=#{openId,jdbcType=VARCHAR}


    and nickName=#{nickName,jdbcType=VARCHAR}


    and gender=#{gender,jdbcType=INTEGER}


    and country=#{country,jdbcType=VARCHAR}


    and province=#{province,jdbcType=VARCHAR}


    and city=#{city,jdbcType=VARCHAR}


    and avatarUrl=#{avatarUrl,jdbcType=VARCHAR}


    and `language`=#{language,jdbcType=VARCHAR}


    and unionId=#{unionId,jdbcType=VARCHAR}


    and sessionKey=#{sessionKey,jdbcType=VARCHAR}


    and lastLoginTime=#{lastLoginTime,jdbcType=TIMESTAMP}


    and updateTime=#{updateTime,jdbcType=TIMESTAMP}


    and inviteUserId=#{inviteUserId,jdbcType=BIGINT}


    limit #{offset},#{limit}

select
count(1)
from user
where 1=1

    and userId=#{userId,jdbcType=BIGINT}


